import{_ as n,o as h,c as t,b as i}from"./app.be380e2e.js";const c={},g=i('<h1 id="loi-noi-\u0111au" tabindex="-1"><a class="header-anchor" href="#loi-noi-\u0111au" aria-hidden="true">#</a> L\u1EDDi n\xF3i \u0111\u1EA7u</h1><p>Ch\xE0o b\u1EA1n,</p><p>Ch\xFAng ta \u0111ang b\u1EAFt \u0111\u1EA7u cu\u1ED9c h\xE0nh tr\xECnh s\xE2u s\u1EAFc v\xE0o th\u1EBF gi\u1EDBi c\u1EE7a C\u1EA5u tr\xFAc D\u1EEF li\u1EC7u v\xE0 Thu\u1EADt to\xE1n, vi\u1EC7c n\u1EAFm v\u1EEFng ch\xFAng l\xE0 quan tr\u1ECDng kh\xF4ng ch\u1EC9 \u0111\u1ED1i v\u1EDBi nh\u1EEFng ng\u01B0\u1EDDi l\u1EADp tr\xECnh vi\xEAn m\u1EDBi, m\xE0 c\xF2n c\u1EA3 \u0111\u1ED1i v\u1EDBi nh\u1EEFng ng\u01B0\u1EDDi c\xF3 kinh nghi\u1EC7m. DSA (Data Structures and Algorithms) l\xE0 m\u1ED9t ch\u1EE7 \u0111\u1EC1 quan tr\u1ECDng trong l\u0129nh v\u1EF1c khoa h\u1ECDc m\xE1y t\xEDnh v\xE0 l\u1EADp tr\xECnh, n\xF3 t\u1EA1o ra n\u1EC1n t\u1EA3ng cho vi\u1EC7c gi\u1EA3i quy\u1EBFt nhi\u1EC1u v\u1EA5n \u0111\u1EC1 ph\u1EE9c t\u1EA1p trong th\u1EBF gi\u1EDBi s\u1ED1 h\xF3a ng\xE0y nay.</p><p>Ebook n\xE0y \u0111\u01B0\u1EE3c t\u1EA1o ra \u0111\u1EC3 gi\xFAp b\u1EA1n hi\u1EC3u s\xE2u h\u01A1n v\u1EC1 C\u1EA5u tr\xFAc D\u1EEF li\u1EC7u v\xE0 Thu\u1EADt to\xE1n, t\u1EEB c\xE1c kh\xE1i ni\u1EC7m c\u01A1 b\u1EA3n cho \u0111\u1EBFn nh\u1EEFng \u1EE9ng d\u1EE5ng th\u1EF1c t\u1EBF. Ch\xFAng t\xF4i \u0111\xE3 c\u1ED1 g\u1EAFng thi\u1EBFt k\u1EBF n\xF3 \u0111\u1EC3 d\u1EC5 hi\u1EC3u v\xE0 \xE1p d\u1EE5ng, d\xF9 b\u1EA1n l\xE0 ng\u01B0\u1EDDi m\u1EDBi b\u1EAFt \u0111\u1EA7u hay b\u1EA1n mu\u1ED1n n\xE2ng cao ki\u1EBFn th\u1EE9c c\u1EE7a m\xECnh.</p><p>Trong ebook n\xE0y, b\u1EA1n s\u1EBD t\xECm hi\u1EC3u v\u1EC1:</p><ul><li>C\xE1c C\u1EA5u tr\xFAc D\u1EEF li\u1EC7u c\u01A1 b\u1EA3n: M\u1EA3ng, danh s\xE1ch li\xEAn k\u1EBFt, c\xE2y, \u0111\u1ED3 th\u1ECB, v.v.</li><li>C\xE1c Thu\u1EADt to\xE1n c\u01A1 b\u1EA3n: T\xECm ki\u1EBFm, s\u1EAFp x\u1EBFp, \u0111\u1EC7 quy, chia \u0111\u1EC3 tr\u1ECB, v.v.</li><li>C\xE1ch x\xE2y d\u1EF1ng v\xE0 t\u1ED1i \u01B0u h\xF3a thu\u1EADt to\xE1n.</li><li>C\xE1ch \xE1p d\u1EE5ng DSA trong c\xE1c b\xE0i to\xE1n th\u1EF1c t\u1EBF.</li><li>H\xE3y nh\u1EDB r\u1EB1ng vi\u1EC7c n\u1EAFm v\u1EEFng C\u1EA5u tr\xFAc D\u1EEF li\u1EC7u v\xE0 Thu\u1EADt to\xE1n kh\xF4ng ch\u1EC9 gi\xFAp b\u1EA1n tr\u1EDF th\xE0nh m\u1ED9t l\u1EADp tr\xECnh vi\xEAn gi\u1ECFi m\xE0 c\xF2n gi\xFAp b\u1EA1n gi\u1EA3i quy\u1EBFt v\u1EA5n \u0111\u1EC1 m\u1ED9t c\xE1ch hi\u1EC7u qu\u1EA3 v\xE0 s\xE1ng t\u1EA1o.</li></ul><p>Ch\xFAng t\xF4i khuy\u1EBFn ngh\u1ECB b\u1EA1n \u0111\u1ECDc t\u1EEBng ph\u1EA7n c\u1EE7a ebook n\xE0y m\u1ED9t c\xE1ch k\u1EF9 l\u01B0\u1EE1ng, th\u1EF1c h\xE0nh v\xE0 h\u1ECFi khi b\u1EA1n g\u1EB7p kh\xF3 kh\u0103n.</p><p>Ch\xFAc b\u1EA1n m\u1ED9t cu\u1ED9c h\xE0nh tr\xECnh h\u1ECDc t\u1ED1t v\xE0 h\xE3y lu\xF4n t\u1EF1 h\xE0o v\u1EC1 s\u1EF1 ph\xE1t tri\u1EC3n c\u1EE7a ki\u1EBFn th\u1EE9c v\xE0 k\u1EF9 n\u0103ng c\u1EE7a m\xECnh trong l\u0129nh v\u1EF1c n\xE0y.</p><p>Ch\xFAng t\xF4i xin ch\xE2n th\xE0nh c\u1EA3m \u01A1n b\u1EA1n \u0111\xE3 l\u1EF1a ch\u1ECDn ebook n\xE0y v\xE0 hy v\u1ECDng n\xF3 s\u1EBD l\xE0 m\u1ED9t t\xE0i li\u1EC7u h\u1EEFu \xEDch trong qu\xE1 tr\xECnh h\u1ECDc t\u1EADp v\xE0 ph\xE1t tri\u1EC3n ngh\u1EC1 nghi\u1EC7p c\u1EE7a b\u1EA1n.</p><p>Tr\xE2n tr\u1ECDng!</p>',10),o=[g];function u(r,a){return h(),t("div",null,o)}var p=n(c,[["render",u],["__file","index.html.vue"]]);export{p as default};
