import{_ as n,o as t,c as h,b as i}from"./app.be380e2e.js";const c={},r=i('<h1 id="loi-noi-\u0111au" tabindex="-1"><a class="header-anchor" href="#loi-noi-\u0111au" aria-hidden="true">#</a> L\u1EDDi n\xF3i \u0111\u1EA7u</h1><p>Xin ch\xE0o v\xE0 hoan ngh\xEAnh b\u1EA1n \u0111\u1EBFn v\u1EDBi trang web n\xE0y, n\u01A1i b\u1EA1n s\u1EBD kh\xE1m ph\xE1 v\xE0 h\u1ECDc c\xE1ch qu\u1EA3n tr\u1ECB C\u01A1 s\u1EDF d\u1EEF li\u1EC7u SQL Server m\u1ED9t c\xE1ch hi\u1EC7u qu\u1EA3. SQL Server l\xE0 m\u1ED9t h\u1EC7 qu\u1EA3n tr\u1ECB c\u01A1 s\u1EDF d\u1EEF li\u1EC7u m\u1EA1nh m\u1EBD v\xE0 ph\u1ED5 bi\u1EBFn \u0111\u01B0\u1EE3c ph\xE1t tri\u1EC3n b\u1EDFi Microsoft, \u0111\u01B0\u1EE3c s\u1EED d\u1EE5ng r\u1ED9ng r\xE3i trong doanh nghi\u1EC7p v\xE0 t\u1ED5 ch\u1EE9c tr\xEAn kh\u1EAFp th\u1EBF gi\u1EDBi.</p><p>D\u1EF1 \xE1n n\xE0y \u0111\u01B0\u1EE3c thi\u1EBFt k\u1EBF \u0111\u1EC3 cung c\u1EA5p cho b\u1EA1n ki\u1EBFn th\u1EE9c v\xE0 k\u1EF9 n\u0103ng c\u1EA7n thi\u1EBFt \u0111\u1EC3 l\xE0m vi\u1EC7c v\u1EDBi SQL Server, t\u1EEB c\u01A1 b\u1EA3n \u0111\u1EBFn n\xE2ng cao. Ch\xFAng t\xF4i s\u1EBD h\u01B0\u1EDBng d\u1EABn b\u1EA1n qua c\xE1c kh\xEDa c\u1EA1nh quan tr\u1ECDng c\u1EE7a vi\u1EC7c qu\u1EA3n tr\u1ECB CSDL SQL Server, bao g\u1ED3m:</p><p>C\xE1ch thi\u1EBFt l\u1EADp v\xE0 c\u1EA5u h\xECnh m\u1ED9t c\u01A1 s\u1EDF d\u1EEF li\u1EC7u SQL Server. Qu\u1EA3n l\xFD b\u1EA3ng d\u1EEF li\u1EC7u, ch\u1EC9nh s\u1EEDa d\u1EEF li\u1EC7u v\xE0 th\u1EF1c hi\u1EC7n truy v\u1EA5n SQL. Hi\u1EC3u c\xE1ch th\u1EF1c hi\u1EC7n sao l\u01B0u v\xE0 ph\u1EE5c h\u1ED3i d\u1EEF li\u1EC7u. \u0110\u1EA3m b\u1EA3o b\u1EA3o m\u1EADt d\u1EEF li\u1EC7u v\xE0 qu\u1EA3n l\xFD ng\u01B0\u1EDDi d\xF9ng v\xE0 ph\xE2n quy\u1EC1n. T\u1ED1i \u01B0u h\xF3a hi\u1EC7u su\u1EA5t h\u1EC7 th\u1ED1ng v\xE0 gi\u1EA3i quy\u1EBFt v\u1EA5n \u0111\u1EC1. Ch\xFAng t\xF4i \u0111\xE3 t\u1ED5 ch\u1EE9c t\xE0i li\u1EC7u v\xE0 h\u01B0\u1EDBng d\u1EABn th\xE0nh t\u1EEBng ph\u1EA7n \u0111\u1EC3 b\u1EA1n d\u1EC5 d\xE0ng ti\u1EBFp c\u1EADn v\xE0 h\u1ECDc h\u1ECFi. B\u1EA5t k\u1EF3 ai mu\u1ED1n tr\u1EDF th\xE0nh m\u1ED9t chuy\xEAn gia v\u1EC1 SQL Server ho\u1EB7c \u0111\u01A1n gi\u1EA3n l\xE0 mu\u1ED1n l\xE0m quen v\u1EDBi vi\u1EC7c qu\u1EA3n tr\u1ECB c\u01A1 s\u1EDF d\u1EEF li\u1EC7u \u0111\u1EC1u c\xF3 th\u1EC3 t\xECm th\u1EA5y th\xF4ng tin h\u1EEFu \xEDch t\u1EA1i \u0111\xE2y.</p><p>H\xE3y b\u1EAFt \u0111\u1EA7u v\xE0 kh\xE1m ph\xE1 th\u1EBF gi\u1EDBi c\u1EE7a SQL Server v\u1EDBi ch\xFAng t\xF4i. Ch\xFAng t\xF4i hy v\u1ECDng r\u1EB1ng b\u1EA1n s\u1EBD t\u1EADn h\u01B0\u1EDFng vi\u1EC7c h\u1ECDc t\u1EADp v\xE0 ph\xE1t tri\u1EC3n k\u1EF9 n\u0103ng m\u1EDBi.</p><p>N\u1EBFu b\u1EA1n c\xF3 b\u1EA5t k\u1EF3 c\xE2u h\u1ECFi ho\u1EB7c \xFD ki\u1EBFn \u0111\xF3ng g\xF3p, xin vui l\xF2ng li\xEAn h\u1EC7 v\u1EDBi ch\xFAng t\xF4i. Ch\xFAng t\xF4i lu\xF4n s\u1EB5n s\xE0ng h\u1ED7 tr\u1EE3 b\u1EA1n.</p><p>Ch\xFAc b\u1EA1n may m\u1EAFn v\xE0 th\xE0nh c\xF4ng trong vi\u1EC7c h\u1ECDc t\u1EADp v\xE0 l\xE0m vi\u1EC7c v\u1EDBi SQL Server!</p><p>Tr\xE2n tr\u1ECDng,</p><p><img src="https://github.com/dangtranhuu/images/blob/main/angurvad/sql-server/banner.png?raw=true" alt="T\u1EA1o repo GitHub"></p><h1 id="tac-gia" tabindex="-1"><a class="header-anchor" href="#tac-gia" aria-hidden="true">#</a> T\xE1c gi\u1EA3</h1><table><thead><tr><th>T\xE1c gi\u1EA3</th><th>N\u1ED9i dung</th><th>S\u1ED1 b\xE0i gi\u1EA3ng</th><th>C\xF4ng c\u1EE5 c\u1EA7n c\xF3</th><th>H\xECnh \u1EA3nh</th></tr></thead><tbody><tr><td><a href="github.com/nguyenkhanhdan">Nguy\u1EC5n Kh\xE1nh \u0110an</a></td><td>N\u1ED9i dung MS Sql Server</td><td>Ch\u01B0a c\xF3</td><td>ch\u01B0a c\xF3</td><td>none</td></tr><tr><td><a href="github.com/theanishtar">Tr\u1EA7n H\u1EEFu \u0110ang</a></td><td>S\u1EEDa \u0111\u1ED5i v\xE0 b\u1ED5 sung</td><td>Ch\u01B0a c\xF3</td><td>ch\u01B0a c\xF3</td><td>none</td></tr></tbody></table>',11),a=[r];function e(u,d){return t(),h("div",null,a)}var v=n(c,[["render",e],["__file","index.html.vue"]]);export{v as default};
